import React, { useState , useEffect} from 'react';
import { useLocation } from 'react-router-dom';
import CreateIssueForm from './CreateIssueForm';
import './css/sprint.css'
import IssueStatus from './issueStatus';
import IssueType from './issuseType';
import Backlog from './Backlog';
// import assignee from "/assignee.png";
useEffect(() => {
  const fetchIssues = async () => {
    try {
      // Fetch issues from the database
      const response = await fetch('your_api_endpoint');
      const data = await response.json();
      // Set the fetched issues to the backlog
      setBacklogIssues(data.issues);
    } catch (error) {
      console.error('Error fetching issues:', error);
    }
  };

  fetchIssues(); // Call the fetchIssues function when the component mounts
}, []);


const Sprint = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const projectId = searchParams.get('projectid');
  console.log(projectId);

  // State to manage form visibility
  const [formOpen, setFormOpen] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [InputField, setInputField] = useState(false);
  const [buttonShow, setbuttonShow] = useState(true);
  const [inputValues, setInputValues] = useState([]);

  // Function to open form
  const openForm = () => {
    setFormOpen(true);
  };

  // Function to close form
  const closeForm = () => {
    setFormOpen(false);
  };
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown); 
  };
  
  const showInputField = () => {
    setInputField(!showDropdown); 
    setbuttonShow(false)
    
  };
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      setInputField(false); 
      setbuttonShow(true)
      setInputValues([...inputValues, event.target.value]);
      event.target.value = ''; 
    }
  };
  console.log("hereee")
 console.log(showDropdown)

  return (
    <>
    <div>
      <h1>Backlog Page</h1>
      <button onClick={openForm}>Create</button>
      {formOpen && <CreateIssueForm projectId={projectId} onClose={closeForm} />}
    </div>
    <div className="mainBox">
      
      
      {/* Additional div with sprint details */}
      <div className="sprint-details">
        <div className="left-section">
          <div className='chotadabba1'>Sprint Name</div >
          <div className='chotadabba'>Start Date</div >
          <div className='chotadabba2'>End Date</div >
        </div>
        <div className="right-section">
          <div>
          <button  >Complete Sprint</button></div>
          
            
            <div className="dropdown" onClick={toggleDropdown}>...</div>
            </div>
            
            {showDropdown && (
              <div className="dropdown-content">
                <span>Edit</span>
                <span>Delete</span>
              </div>
            )}
          
        </div>
     

        {/* here i wanna pass filtered issue */}
      <Backlog /> 
      </div>
      {/* here i wanna pass all the  issue */}
    <Backlog />
    </>
  );
};

export default Sprint;
