import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { connect } from 'react-redux';
import axios from 'axios';

const Filters = ({ isAuthenticated, user }) => {
  const { projectid } = useParams();
  const [selectedFilter, setSelectedFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [currentUser, setCurrentUser] = useState('');
  const [data, setData] = useState([]);

  useEffect(() => {
    if (isAuthenticated && user) {
      setCurrentUser(user.email);
    }
  }, [isAuthenticated, user]);

  useEffect(() => {
    if (selectedFilter) {
      const payload = { filter: selectedFilter, status: selectedFilter === 'Status' ? statusFilter : '', projectid, currentUser };

      axios.post('http://localhost:8000/get_filtered_issues_or_epics/', payload)
        .then(response => {
          setData(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the data!", error);
        });
    }
  }, [selectedFilter, statusFilter, projectid, currentUser]);

  return (
    <div>
      <h1>Filters</h1>
      <select
        value={selectedFilter}
        onChange={(e) => {
          setSelectedFilter(e.target.value);
          if (e.target.value !== 'Status') {
            setStatusFilter('');
          }
        }}
      >
        <option value="">Select Filter</option>
        <option value="all_issues">All issues</option>
        <option value="assigned_to_me">Assigned to me</option>
        <option value="Status">Status</option>
        <option value="unassigned">Unassigned</option>
        <option value="epics">Epics</option>
      </select>

      {selectedFilter === 'Status' && (
        <div>
          <h5>Status</h5>
          <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)}>
            <option value="">Select Status</option>
            <option value="to_do">To Do</option>
            <option value="in_progress">In Progress</option>
            <option value="done">Done</option>
          </select>
        </div>
      )}

      {data.map(item => (
        <div key={item.issue_id || item.Epic_id}>
          <h3>{item.IssueName || item.EpicName}</h3>
          <p>{item.description}</p>
        </div>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
  user: state.auth.user,
});

export default connect(mapStateToProps)(Filters);
