import React, { useState } from 'react';
import './css/sprint.css';
import IssueType from './issuseType';
import IssueStatus from './issueStatus';

export default function Backlog() {
  const [inputValues, setInputValues] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const [InputField, setInputField] = useState(false);
  const [buttonShow, setbuttonShow] = useState(true);
  const [draggedEle, setDragged] = useState([]);

  const showInputField = () => {
    setInputField(!showDropdown);
    setbuttonShow(false);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      setInputField(false);
      setbuttonShow(true);
      setInputValues([...inputValues, event.target.value]);
      event.target.value = '';
    }
  };

  const drop = (e) => {
    setDragged((prevEles) => [
      ...prevEles,
      { value: e.target.textContent, type: 'Issue Type', icon: '/assignee.png' },
    ]);
  };

  return (
    <div>
      <div className="mainBox">
        <div className={inputValues.length ? 'solid-box' : 'dotted-box'}>
          {inputValues.map((value, index) => (
            <div key={index} className="input-item" draggable onDragEnd={drop}>
              <div>{value}</div>
              <div className="right">
                <IssueStatus />
              </div>
              <img src="/assignee.png" alt="assignee" id="userIcon" />
            </div>
          ))}
        </div>
        <div className="create-issue">
          {buttonShow && <button onClick={showInputField}>Create Issue</button>}
          {InputField && (
            <div className="issueCreation">
              <IssueType />
              <input type="text" placeholder="type your issue here" onKeyDown={handleKeyPress} />
            </div>
          )}
        </div>
      </div>
      <div className="listOfIssue">
        {draggedEle.map((element, index) => (
          <div key={index} className="dragged-item">
            <div>{element.value}</div>
            <IssueType />
            <img src={element.icon} alt="assignee" id="userIcon" />
          </div>
        ))}
      </div>
    </div>
  );
}
