import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import CreateIssueForm from './CreateIssueForm';
import "./css/backlog"

const Backlog = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const projectId = searchParams.get('projectid');
  console.log(projectId);

  // State to manage form visibility
  const [formOpen, setFormOpen] = useState(false);

  // Function to open form
  const openForm = () => {
    setFormOpen(true);
  };

  // Function to close form
  const closeForm = () => {
    setFormOpen(false);
  };

  return (
    <>
    <div>
      <h1>Backlog Page</h1>
      <button onClick={openForm}>Create</button>
      {formOpen && <CreateIssueForm projectId={projectId} onClose={closeForm} />}
    </div>
    <div>
      <h1>Backlog Page</h1>
      <button onClick={openForm}>Create</button>
      {formOpen && <CreateIssueForm projectId={projectId} onClose={closeForm} />}
      
      {/* Additional div with sprint details */}
      <div className="sprint-details">
        <div className="left-section">
          <span>Sprint Name</span>
          <span>Start Date</span>
          <span>End Date</span>
        </div>
        <div className="right-section">
          <button>Complete Sprint</button>
          <div className="dropdown" onClick={() => setShowDropdown(!showDropdown)}>
            {/* This is the ... icon */}
            <span>...</span>
            {/* Dropdown content */}
            {showDropdown && (
              <div className="dropdown-content">
                <span>Edit</span>
                <span>Delete</span>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Second div with dotted box */}
      <div className="dotted-box"></div>

      {/* Third div with create issue button */}
      <div className="create-issue">
        <button>Create Issue</button>
      </div>
    </div>
    </>
  );
};

export default Backlog;
