import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DisplayIssueFilters = ({ data }) => {
  const [issue, setIssue] = useState(data);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    setIssue(data);
  }, [data]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setIssue(prevIssue => ({
      ...prevIssue,
      [name]: value
    }));
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = async () => {
    try {
      await axios.post(`http://localhost:8000/djapp/update_issue/`, issue);
      setIsEditing(false);
    } catch (error) {
      console.error("There was an error updating the issue!", error);
    }
  };

  return (
    <div>
      {issue ? (
        <div className="issue-card">
          <h1>{issue.IssueName}</h1>
          <p>
            <strong>Type:</strong>
            {isEditing ? (
              <select name="IssueType" value={issue.IssueType} onChange={handleChange}>
                <option value="">Select...</option>
                <option value="Story">Story</option>
                <option value="Task">Task</option>
                <option value="Bug">Bug</option>
                <option value="Epic">Epic</option>
              </select>
            ) : (
              issue.IssueType
            )}
          </p>
          <p>
            <strong>Description:</strong>
            {isEditing ? (
              <textarea name="description" value={issue.description} onChange={handleChange} />
            ) : (
              issue.description
            )}
          </p>
          <p>
            <strong>Status:</strong>
            {isEditing ? (
            <select name="IssueType" value={issue.IssueType} onChange={handleChange}>
            <option value="">Select...</option>
            <option value="Story">To Do</option>
            <option value="Task">In Progress</option>
            <option value="Bug">Done</option>
          </select>
              
            ) : (
              issue.status
            )}
          </p>
          <p>
            <strong>Assigned to:</strong>
            {isEditing ? (
              <input name="assignee" value={issue.assignee} onChange={handleChange} />
            ) : (
              issue.assignee
            )}
          </p>
          <p>
            <strong>Assigned by:</strong>
            {isEditing ? (
              <input name="assigned_by" value={issue.assigned_by} onChange={handleChange} />
            ) : (
              issue.assigned_by
            )}
          </p>
          {issue.assigned_epic_id && (
            <p>
              <strong>Epic:</strong>
              {isEditing ? (
                <input name="assigned_epic_id" value={issue.assigned_epic_id} onChange={handleChange} />
              ) : (
                issue.assigned_epic_id
              )}
            </p>
          )}
          {issue.sprint_id && (
            <p>
              <strong>Sprint:</strong>
              {isEditing ? (
                <input name="sprint_id" value={issue.sprint_id} onChange={handleChange} />
              ) : (
                issue.sprint_id
              )}
            </p>
          )}
          <p>
            <strong>Project ID:</strong>
            {isEditing ? (
              <input name="projectId_id" value={issue.projectId_id} onChange={handleChange} />
            ) : (
              issue.projectId_id
            )}
          </p>
          {issue.file_field && (
            <p>
              <strong>File:</strong>
              {isEditing ? (
                <input name="file_field" value={issue.file_field} onChange={handleChange} />
              ) : (
                <a href={`/path/to/files/${issue.file_field}`}>{issue.file_field}</a>
              )}
            </p>
          )}
          {isEditing ? (
            <button onClick={handleSave}>Save</button>
          ) : (
            <button onClick={handleEdit}>Edit</button>
          )}
        </div>
      ) : (
        <p>No issues found</p>
      )}
    </div>
  );
};

export default DisplayIssueFilters;
