import React, { useState } from 'react';
import './css/sprint.css';
import IssueType from './issuseType';
import { connect } from 'react-redux'; // Import connect from react-redux
import { addIssue } from '../actions/auth';
import IssueStatus from './issueStatus';

const Backlog = ({ addIssue ,project_id}) =>  {
  const [inputValues, setInputValues] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const [InputField, setInputField] = useState(false);
  const [buttonShow, setButtonShow] = useState(true);
  const [draggedEle, setDragged] = useState([]);


  const { projectid } = useParams();

  useEffect(() => {
      // Check if project data exists in local storage
      const projectData = JSON.parse(localStorage.getItem('project'));
      if (projectData) {
          // If project data exists, update Redux state
          fetchProject(projectData);
      } else {
          // If project data doesn't exist, fetch project details from backend
          fetchProject(projectId);
      }
  }, [projectId, fetchProject]);

  const showInputField = () => {
    setInputField(!showDropdown);
    setButtonShow(false);
  };
  

  const handleKeyPress = async (event) => {
    if (event.key === 'Enter') {
      setInputField(false);
      setButtonShow(true);
      setInputValues([...inputValues, event.target.value]);
      
      try {
        await addIssue({ issueName: event.target.value });
      } catch (error) {
        console.error('Error creating project:', error);
      }
      event.target.value = '';
    }
};

  return (
    <>
    <h1>{projectid}</h1>
      <div className={inputValues.length ? 'solid-box' : 'dotted-box'}>
        {inputValues.map((value, index) => (
          <div key={index} className="input-item" >
            <div className='value'>{value}</div>
            <div className="right">
              <IssueStatus />
            </div>
            <img src="/assignee.png" alt="assignee" id="userIcon" />
          </div>
        ))}
      </div>
      <div className="create-issue">
        {buttonShow && <button onClick={showInputField}>Create Issue</button>}
        {InputField && (
          <div className="issueCreation">
            <IssueType />
            <input type="text" placeholder="type your issue here" onKeyDown={handleKeyPress} />
          </div>
        )}
      </div>
    </>
  );
}

const mapStateToProps = (state) => ({
 
  projectid:state.auth.project.projectid
});

export default connect(mapStateToProps, { addIssue })(Backlog); // Connect the component to the Redux store and add the addIssue action
