import React, { useState , useEffect} from 'react';
import { useLocation, useParams } from 'react-router-dom';
import CreateIssueForm from './CreateIssueForm';
import './css/sprint.css'
import IssueStatus from './issueStatus';
import IssueType from './issuseType';
import Backlog from './Backlog';



const Sprint = () => {
 const {projectid}=useParams()

  useEffect(() => {
    // const fetchIssues = async () => {
      
    //   try {
    //     const response = await fetch(`http://localhost:8000/djapp/issues/?project_id=${projectid}/`);
    //     if (response.ok) {
    //       const data = await response.json();
    //       // setIssues(data); // Assuming data is an array of issues
    //     } else {
    //       console.error('Failed to fetch issues:', response.status);
    //     }
    //   } catch (error) {
    //     console.error('Error fetching issues:', error);
    //   }
    // };
    const fetchIssues = async () => {
      try {
        const queryParams = new URLSearchParams({ project_id: projectid });
        const url = `http://localhost:8000/djapp/issues/?${queryParams.toString()}`;
    
        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        });
    
        if (response.ok) {
          const data = await response.json();
          // Process the fetched data
        } else {
          console.error('Failed to fetch issues:', response.status);
        }
      } catch (error) {
        console.error('Error fetching issues:', error);
      }
    };
    

    fetchIssues(); // Fetch issues when component mounts
  }, [projectid]); 

  
  const [formOpen, setFormOpen] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [InputField, setInputField] = useState(false);
  const [buttonShow, setbuttonShow] = useState(true);
  const [inputValues, setInputValues] = useState([]);

 
  const openForm = () => {
    setFormOpen(true);
  };

  // Function to close form
  const closeForm = () => {
    setFormOpen(false);
  };
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown); 
  };
  
  console.log("hereee")
 console.log(showDropdown)

  return (
    <>
    <div>
      <h1>Backlog Page</h1>
      <button onClick={openForm}>Create</button>
      {formOpen && <CreateIssueForm projectId={projectid} onClose={closeForm} />}
    </div>
    <div className="mainBox">
      
      
      {/* Additional div with sprint details */}
      <div className="sprint-details">
        <div className="left-section">
          <div className='chotadabba1'>Sprint Name</div >
          <div className='chotadabba'>Start Date</div >
          <div className='chotadabba2'>End Date</div >
        </div>
        <div className="right-section">
          <div>
          <button  >Complete Sprint</button></div>
          
            
            <div className="dropdown" onClick={toggleDropdown}>...</div>
            </div>
            
            {showDropdown && (
              <div className="dropdown-content">
                <span>Edit</span>
                <span>Delete</span>
              </div>
            )}
          
        </div>
     

        {/* here i wanna pass filtered issue */}
      <Backlog /> 
      </div>
      {/* here i wanna pass all the  issue */}
    <Backlog />
    </>
  );
};

export default Sprint;
